    """Enhanced Chrome headless PDF generation with optimized settings"""

    chrome_commands = [
        "/Applications/Google Chrome.app/Contents/MacOS/Google Chrome",
        "google-chrome",
        "chromium",
        "google-chrome-stable",
        "/opt/homebrew/bin/chromium",
        "/usr/local/bin/google-chrome",
    ]

    for chrome_cmd in chrome_commands:
        try:
            if chrome_cmd.startswith("/") and not Path(chrome_cmd).exists():
                continue

            # Enhanced Chrome settings for professional PDF quality
            cmd = [
                chrome_cmd,
                "--headless=new",
                "--disable-gpu",
                "--disable-dev-shm-usage",
                "--no-sandbox",
                "--disable-background-timer-throttling",
                "--disable-renderer-backgrounding",
                "--disable-backgrounding-occluded-windows",

                # PDF generation settings
                f"--print-to-pdf={pdf_path}",
                "--print-to-pdf-no-header",
                "--no-pdf-header-footer",
                "--no-margins",

                # Rendering quality
                "--force-color-profile=srgb",
                "--disable-background-media-suspend",
                "--disable-features=TranslateUI",
                "--disable-ipc-flooding-protection",

                # Print-specific optimizations
                "--disable-extensions",
                "--disable-plugins",
                "--virtual-time-budget=10000",
                "--run-all-compositor-stages-before-draw",

                # Layout & sizing
                "--window-size=1280,1696",
                "--hide-scrollbars",
                "--disable-background-networking",

                # Font & asset handling
                "--font-render-hinting=none",
                "--enable-font-antialiasing",
                "--disable-remote-fonts",
                "--allow-file-access-from-files",

                f"file://{html_path.absolute()}",
            ]

            result = subprocess.run(
                cmd, 
                capture_output=True, 
                text=True, 
                timeout=30,
                cwd=html_path.parent
            )

            if result.returncode == 0 and pdf_path.exists() and pdf_path.stat().st_size > 10000:
                print(f"‚úÖ Enhanced PDF generated: {pdf_path}")
                print(f"üìä File size: {pdf_path.stat().st_size // 1024}KB")
                return True
            else:
                error_msg = result.stderr if result.stderr else "Unknown error"
                print(f"‚ùå Chrome failed with {chrome_cmd}: {error_msg}")

        except subprocess.TimeoutExpired:
            print(f"‚è∞ Chrome timeout with {chrome_cmd}")
            continue
        except FileNotFoundError:
            continue
        except Exception as e:
            print(f"üí• Chrome error with {chrome_cmd}: {e}")
            continue

    return False

def build_pdf_enhanced(self, target_version: str, template: str = "francois") -> None:
